library(tidyllm)

classify_image &lt;- function(image, task) {
  tidyllm::llm_message(task, .imagefile = image) |&gt;
    tidyllm::ollama(
      .ollama_server = "https://llm.ifp.uni-mainz.de",
      .model = "llava:34b",
      .temperature = 0
    ) |&gt;
    tidyllm::last_reply() |&gt;
    str_squish()
}





